{% block title %} Ajouter un cours {% endblock %}

{% block styles %}
    <style>
        .form-container {
            width: 50%;
            margin: 20px auto;
            padding: 20px;
            background: #f9f9f9;
            border-radius: 8px;
            box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
        }

        h1 {
            text-align: center;
            color: #333;
        }

        label {
            font-weight: bold;
            margin-top: 10px;
            display: block;
        }

        input, textarea {
            width: 100%;
            padding: 8px;
            margin-top: 5px;
            border: 1px solid #ccc;
            border-radius: 5px;
        }

        button {
            width: 100%;
            background: #007bff;
            color: white;
            padding: 10px;
            border: none;
            border-radius: 5px;
            margin-top: 15px;
            cursor: pointer;
        }

        button:hover {
            background: #0056b3;
        }

        .back-link {
            display: block;
            text-align: center;
            margin-top: 15px;
            color: #007bff;
            text-decoration: none;
            font-weight: bold;
        }

        .back-link:hover {
            text-decoration: underline;
        }
    </style>
{% endblock %}

{% block body %}
    <div class=\"form-container\">
        <h1>Ajouter un cours</h1>

        {{ form_start(form) }}
            <label for=\"titre\">Titre du cours</label>
            {{ form_widget(form.titre) }}

            <label for=\"description\">Description</label>
            {{ form_widget(form.description) }}

            <label for=\"contenu\">Contenu</label>
            {{ form_widget(form.contenu) }}

            <button type=\"submit\">Enregistrer</button>
        {{ form_end(form) }}

        <a href=\"{{path('app_cours') }}\" class=\"back-link\">Retour à la liste</a>
    </div>
{% endblock %}
", "addcours.html.twig", "C:\\Users\\yassi\\Downloads\\Novalearn-master\\Novalearn-master\\templates\\addcours.html.twig");
